import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css';
import Navbar from './components/Navbar/Navbar';
import Contacto from './Screens/Contacto/Contacto';
import Inicio from './Screens/Inicio/Inicio';
import Productos from './Screens/Productos/Productos';
import Nosotros from './Screens/Nosotros/Nosotros';
import NuestrosClientes from './Screens/NuestrosClientes/NuestrosClientes';
import ProductDetail from './components/ProductDetail/ProductDetail';
import Footer from './components/Footer/Footer';
import PaginaEnConstruccion from './Screens/PaginaEnConstruccion/PaginaEnConstruccion';

function App() {
  return (
    <Router>
      <div style={{ display: "flex", flexDirection: "column", minHeight: "100vh" }}>
        <Navbar />
        <main style={{ flex: 1 }}>
          <Routes>

            {/* RUTA PRINCIPAL COMO LANDING */}
            <Route path="/" element={
              <div>
                <section id="Inicio">
                  <Inicio />
                </section>
                <section id="Productos">
                  <Productos />
                </section>
                <section id="NuestrosClientes">
                  <NuestrosClientes />
                </section>
                <section id="Nosotros">
                  <Nosotros />
                </section>
                <section id="Contacto">
                  <Contacto />
                </section>
              </div>
            } />

            {/* PÁGINAS EXTERNAS QUE SÍ TIENEN SU RUTA */}
            <Route path="/product/:id" element={<ProductDetail />} />
            <Route path="/construccion" element={<PaginaEnConstruccion />} />

          </Routes>
        </main>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
