import React from "react";
import { Container } from "react-bootstrap";
import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation, Autoplay } from "swiper/modules";
import "swiper/css";
import "swiper/css/navigation";
import logo from '../../assets/img/logo.png';
import './NuestrosClientes.css';

const NuestrosClientes = () => {
  const empresas = [
    { nombre: "La Cumbre", logo: logo },
    { nombre: "La Cumbre", logo: logo },
    { nombre: "La Cumbre", logo: logo },
    { nombre: "La Cumbre", logo: logo },
    { nombre: "La Cumbre", logo: logo },
    { nombre: "La Cumbre", logo: logo },
    { nombre: "La Cumbre", logo: logo },
    { nombre: "La Cumbre", logo: logo },
    { nombre: "La Cumbre", logo: logo },
    { nombre: "La Cumbre", logo: logo },
    { nombre: "La Cumbre", logo: logo },
    { nombre: "La Cumbre", logo: logo },

  ];

  return (
    <Container className="text-center my-5">
      <h2 className="mb-4">EMPRESAS QUE CONFÍAN EN NOSOTROS</h2>
      <Swiper
        modules={[Navigation, Autoplay]}
        navigation={true}
        autoplay={{ delay: 3000, disableOnInteraction: false }}
        slidesPerView={2} // 2 filas de 6
        grid={{ rows: 2, fill: "row" }}
        spaceBetween={20}
        breakpoints={{
          320: { slidesPerView: 2 }, // Para móviles pequeños
          576: { slidesPerView: 3 },  // Para móviles más grandes
          768: { slidesPerView: 4 },  // Para tablets
          1024: { slidesPerView: 6 }  // Para escritorios
        }}
      >
        {empresas.map((empresa, index) => (
          <SwiperSlide key={index}>
            <img
              src={empresa.logo}
              alt={`Logo de ${empresa.nombre}`} // Mejorar accesibilidad
              className="client-logo img-fluid"
            />
          </SwiperSlide>
        ))}
      </Swiper>
    </Container>
  );
};

export default NuestrosClientes;
