import React from "react";
import { Container } from "react-bootstrap";
import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation, Autoplay } from "swiper/modules";
import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/grid"; // Asegura que Swiper maneje el diseño en filas
import logo from "../../assets/img/logo.png";
import "./NuestrosClientes.css";

const NuestrosClientes = () => {
  const empresas = Array(12).fill({ nombre: "La Cumbre", logo: logo });

  return (
    <Container className="text-center my-5">
      <h2 className="mb-4">EMPRESAS QUE CONFÍAN EN NOSOTROS</h2>
      <Swiper
        modules={[Navigation, Autoplay]}
        navigation={{
          nextEl: ".swiper-button-next",
          prevEl: ".swiper-button-prev",
        }}
        autoplay={{ delay: 5000, disableOnInteraction: false }}
        spaceBetween={10}
        breakpoints={{
          320: { slidesPerView: 2, slidesPerGroup: 2 },  // Teléfonos pequeños
          576: { slidesPerView: 3, slidesPerGroup: 3 },  // Teléfonos grandes
          768: { slidesPerView: 4, slidesPerGroup: 4 },  // Tablets
          1024: { slidesPerView: 6, slidesPerGroup: 6 }, // Escritorios
        }}
      >
        {empresas.map((empresa, index) => (
          <SwiperSlide key={index} className="slide">
            <img
              src={empresa.logo}
              alt={`Logo de ${empresa.nombre}`}
              className="client-logo img-fluid"
            />
          </SwiperSlide>
        ))}
      </Swiper>

      {/* Botones de navegación personalizados */}
      <div className="swiper-button-prev"></div>
      <div className="swiper-button-next"></div>
    </Container>
  );
};

export default NuestrosClientes;
