import React from "react";
import { Container } from "react-bootstrap";
import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation, Autoplay } from "swiper/modules";
import "swiper/css";
import "swiper/css/navigation";
import logo from '../../assets/img/logo.png';

const NuestrosClientes = () => {
  const empresas = [
    { nombre: "La Cumbre", logo: logo },
    { nombre: "La Cumbre", logo: logo },
    { nombre: "La Cumbre", logo: logo },
    { nombre: "La Cumbre", logo: logo },
    { nombre: "La Cumbre", logo: logo },
    { nombre: "La Cumbre", logo: logo },
    { nombre: "La Cumbre", logo: logo },
    { nombre: "La Cumbre", logo: logo },
    { nombre: "La Cumbre", logo: logo },
    { nombre: "La Cumbre", logo: logo },
    { nombre: "La Cumbre", logo: logo },
    { nombre: "La Cumbre", logo: logo },

  ];
  
// Dividimos en dos filas
const fila1 = empresas.slice(0, 6);
const fila2 = empresas.slice(6, 12);

return (
  <Container className="text-center my-5">
    <h2 className="mb-4">EMPRESAS QUE CONF√çAN EN NOSOTROS</h2>

    {/* Primera fila */}
    <Swiper
      modules={[Navigation, Autoplay]}
      navigation={true}
      autoplay={{ delay: 3000, disableOnInteraction: false }}
      slidesPerView={6} // 6 columnas
      spaceBetween={20}
    >
      {fila1.map((empresa, index) => (
        <SwiperSlide key={index}>
          <img
            src={empresa.logo}
            alt={empresa.nombre}
            className="img-fluid"
            style={{ maxHeight: "100px" }}
          />
        </SwiperSlide>
      ))}
    </Swiper>

    {/* Segunda fila */}
    <Swiper
      modules={[Navigation, Autoplay]}
      navigation={true}
      autoplay={{ delay: 3000, disableOnInteraction: false }}
      slidesPerView={6} // 6 columnas
      spaceBetween={20}
    >
      {fila2.map((empresa, index) => (
        <SwiperSlide key={index}>
          <img
            src={empresa.logo}
            alt={empresa.nombre}
            className="img-fluid"
            style={{ maxHeight: "100px" }}
          />
        </SwiperSlide>
      ))}
    </Swiper>
  </Container>
);
};

export default NuestrosClientes;
