import React, { useState } from 'react';
import { Link as ScrollLink } from 'react-scroll'; // Para desplazamiento suave
import styles from './NavbarStyle.module.css';  // CSS Modules
import logo from '../../assets/img/logo.jpg';

const Navbar = () => {
  const [menuOpen, setMenuOpen] = useState(false);

  const sections = [
    { name: "Inicio", id: "Inicio" },
    { name: "Productos", id: "Productos" },
    { name: "Quienes Somos", id: "QuienesSomos" },
    { name: "Nuestros Clientes", id: "NuestrosClientes" },
    { name: "Contacto", id: "Contacto" },
  ];

  return (
    <nav className={styles.navbar}>
      <div className={styles.navbarContainer}>
        {/* Logo */}
        <a href="/" className={styles.navbarBrand}>
          <img src={logo} alt="Logo" className={styles.logo} />
        </a>

        {/* Botón de Menú (Hamburguesa) */}
        <button
          className={styles.menuButton}
          onClick={() => setMenuOpen(!menuOpen)}
        >
          ☰
        </button>

        {/* Menú de Navegación con clase dinámica */}
        <ul className={`${styles.navbarLinks} ${menuOpen ? styles.active : ""}`}>
          {sections.map((section) => (
            <li key={section.id} className={styles.navItem}>
              <ScrollLink
                className={styles.navLink}
                to={section.id}
                smooth={true}
                duration={500}
                onClick={() => setMenuOpen(false)} // Cerrar menú al hacer clic en un enlace
              >
                {section.name}
              </ScrollLink>
            </li>
          ))}
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
