import React, { useState, useEffect } from "react";
import { Link as ScrollLink } from "react-scroll";
import { Link as RouterLink, useLocation } from "react-router-dom";
import logo from "../../assets/img/logo.jpg"; // Ajusta la ruta a tu logo
import styles from './Navbar.module.css';

const Navbar = () => {
  const [scrolled, setScrolled] = useState(false);
  const location = useLocation();

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 50);
    };

    if (location.hash) {
      const section = location.hash.substring(1);
      const element = document.getElementById(section);
      if (element) {
        element.scrollIntoView({ behavior: "smooth", block: "start" });
      }
    }

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, [location]);

  const sections = [
    { name: "Inicio", id: "Inicio" },
    { name: "Nuestros Productos", id: "NuestrosProductos" },
    { name: "Sostenibilidad", id: "Sostenibilidad" },
    { name: "Comercialización", id: "Comercializacion" },
    { name: "Contacto", id: "Contacto" },
  ];

  return (
    <nav
      className={`${styles.navbar} navbar navbar-expand-sm fixed-top ${
        scrolled ? styles.solid : styles.transparent
      }`}
    >
      <div className="container-fluid">
        <RouterLink to="/" className={styles["navbar-brand"]}>
          <img src={logo} alt="Logo" className={styles.logo} />
        </RouterLink>
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
          aria-controls="navbarNav"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon" />
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className={`navbar-nav ms-auto ${styles['navbar-nav']}`}> {/* Agregamos la clase de Navbar.module.css */}
            <li className={`nav-item ${styles['nav-item']}`}> {/* Agregamos la clase de Navbar.module.css */}
              {location.pathname === "/" ? (
                <ScrollLink
                  className={`${styles["nav-link"]} nav-link`}
                  to="Inicio"
                  smooth={true}
                  duration={500}
                >
                  INICIO
                </ScrollLink>
              ) : (
                <RouterLink
                  to="/#Inicio"
                  className={`${styles["nav-link"]} nav-link`}
                >
                  INICIO
                </RouterLink>
              )}
            </li>
            <li className={`nav-item ${styles['nav-item']}`}> {/* Agregamos la clase de Navbar.module.css */}
              <RouterLink
                to="/nosotros"
                className={`${styles["nav-link"]} nav-link`}
              >
                NOSOTROS
              </RouterLink>
            </li>
            {sections
              .filter((section) => section.name !== "Inicio")
              .map((section) => (
                <li className={`nav-item ${styles['nav-item']}`} key={section.id}> {/* Agregamos la clase de Navbar.module.css */}
                  {location.pathname === "/" ? (
                    <ScrollLink
                      className={`${styles["nav-link"]} nav-link`}
                      to={section.id}
                      smooth={true}
                      duration={500}
                    >
                      {section.name.toUpperCase()}
                    </ScrollLink>
                  ) : (
                    <RouterLink
                      to={`/#${section.id}`}
                      className={`${styles["nav-link"]} nav-link`}
                    >
                      {section.name.toUpperCase()}
                    </RouterLink>
                  )}
                </li>
              ))}
          </ul>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
